mapper.select:
SELECT link_type.type_name as type_name,image.img_path as img_path, link.* FROM tg_cms_m_ext_friendlink link
    LEFT JOIN tg_cms_m_ext_friendlink_type link_type ON link.type_id = link_type.id
    LEFT JOIN tg_cms_m_base_image image ON link.fl_logo = image.id

        link_type.code_num=#{typeCodeNum}


    LIMIT 0,#{limit}

ORDER BY sort_order ASC

SELECT link_type.type_name as type_name,image.img_path as img_path, link.* FROM tg_cms_m_ext_friendlink link LEFT JOIN tg_cms_m_ext_friendlink_type link_type ON link.type_id = link_type.id LEFT JOIN tg_cms_m_base_image image ON link.fl_logo = image.id
link.type_id=#{typeId}
ORDER BY sort_order ASC

SELECT link_type.type_name as type_name,image.img_path as img_path, link.* FROM tg_cms_m_ext_friendlink link LEFT JOIN tg_cms_m_ext_friendlink_type link_type ON link.type_id = link_type.id LEFT JOIN tg_cms_m_base_image image ON link.fl_logo = image.id WHERE link.enabled = 1
AND link.type_id=#{typeId}
ORDER BY sort_order ASC

SELECT linktype.type_name as type_name,link.* FROM tg_cms_m_ext_friendlink link
    LEFT JOIN tg_cms_m_ext_friendlink_type linktype ON link.type_id = linktype.id
WHERE link.id = #{id}

mapper.insert:
INSERT INTO tg_cms_m_ext_friendlink (
    id, type_id, fl_name, fl_url, fl_logo, 
    fl_email, recommend, sort_order, description, enabled,
    create_data_time, create_data_username
)
VALUES(
    #{id}, #{typeId}, #{flName}, #{flUrl}, #{flLogo}, 
    #{flEmail}, #{recommend}, #{sort_order}, #{description}, #{enabled},
    #{createDataTime}, #{createDataUsername}
)
